{
	"id": "MyThumbnailAppBundle",
	"description": "App bundle generating thumbnails from IPT files.",
	"engine": "Autodesk.Inventor+23"
}

{
    "commandLine": [
        "$(engine.path)\\InventorCoreConsole.exe /i $(args[PartFile].path) /al $(appbundles[ThumbnailAppBundle].path)"
    ],
    "parameters": {
        "PartFile": {
            "verb": "get",
            "description": "IPT file or ZIP with assembly to process"
        },
        "OutputBmp": {
            "zip": false,
            "ondemand": false,
            "optional": true,
            "verb": "put",
            "description": "Generated thumbnail",
            "localName": "thumbnail.bmp"
        }
    },
    "engine": "Autodesk.Inventor+23",
    "appbundles": ["YhryNMLor4R1maFhY4zER8unpISoP5E4.ThumbnailAppBundle+prod"],
    "description": "Generate thumbnails for IPT files (Inventor 2019).",
    "id": "GenerateThumbnail"
}

{
    "documents": [
        {
            "item": {
                "sourceId": {
                    "sourceSystem": "exampleDataRoot",
                    "id": "id200",
                    "type": "fs.file"
                },
                "securityInfo": {
                    "securityType": "open"
                },
                "sourceVersion": "1",
                "creator": {
                    "userIdValue": "uid2",
                    "userIdType": "adsk.oxygen"
                },
                "lastModifier": {
                    "userIdValue": "uid3",
                    "userIdType": "adsk.oxygen"
                },
                "dateCreated": "2012-01-01T12:31:12",
                "dateModified": "2012-02-02T13:15:15",
                "content": {
                    "textContent": "the quick brown fox jumps over the lazy dogs",
                    "name": "first example13 name"
                }
            }
        }
    ]
}
